    function noexp(number, digits) {
        var digits = digits || 4;
        var amount = getNoExpValue(number);
        var position = amount.toString().indexOf('.');

        if (position >= 0) {
            var amountStr = amount.toString().substr(0, position + digits+1);
            digits >= 0 && (amountStr = amountStr.replace(/0+$/, '0'));
            return +amountStr === 0 ? 0 : amountStr;
        } else {
            return amount;
        }

        function getNoExpValue(value) {
            var data = String(value).split(/[eE]/);
            if(data.length== 1) return data[0];

            var z= '', sign = value<0? '-':'',
                str= data[0].replace('.', ''),
                mag= Number(data[1])+ 1;

            if(mag<0){
                z= sign + '0.';
                while(mag++) z += '0';
                return z + str.replace(/^\-/,'');
            }
            mag -= str.length;
            while(mag--) z += '0';
            return str + z;
        }
    }

