    ModalWindow.prototype.close = function() {
        document.body.classList.remove('gold-modal-opened');
        typeof this.opened.modal.onDestroy === 'function' && this.opened.modal.onDestroy.call(this);
        this.opened = null;
        this.globalModalData = {};
    };

    /**
     * Initializes form modal
     */
    ModalWindow.prototype.formInit = function() {
        var self = this;
        clearInterval(this.updateTokenPriceIntervel);

        /**
         * Update token price in modal window
         */
        function updateTokenPriceInModal() {
            var interval = setInterval(function() {
                var buyPriceElem = document.querySelector("[data-bind='tokenBuyPrice']"),
                    sellPriceElem = document.querySelector("[data-bind='tokenSellPrice']"),
                    buyPriceValue = buyPriceElem ? buyPriceElem.textContent : null,
                    sellPriceValue = sellPriceElem ? sellPriceElem.textContent : null;

                if (+buyPriceValue) {
                    clearInterval(interval);
                    self.binds.update({
                        modalBuyTokenPrice: buyPriceValue,
                        modalSellTokenPrice: sellPriceValue
                    });
                }
            }, 100);
        }
        updateTokenPriceInModal();
        this.updateTokenPriceIntervel = setInterval(function () { updateTokenPriceInModal() }, 60000);

        /**
         * Update bindings
         */
        this.binds.update({
            tokenTicker: this.globalModalData.tokenInfo.ticker,
            logoURI: this.globalModalData.tokenInfo.logoUrl,
            isBuy: this.opened.direction === 'buy',
            isSell: this.opened.direction === 'sell',
            loading: true
        });

        function openForm(direction) {
            delete this.modalData.fromAmount;
            delete this.modalData.toAmount;
            this.open('form', direction);
        }

        /**
         * Attach event
         */
        this.binds.event('click', {
            openBuy: openForm.bind(this, 'buy'),
            openSell: openForm.bind(this, 'sell'),
            next: function() {
                if (!self.metamask.isInstalled) {
                    return self.open('noMetaMask');
                }

                if (!self.metamask.isLoggedIn) {
                    self.globalModalData.formDirection = self.opened.direction;
                    return self.open('loginMetaMask');
                }

                function transactionCompleted(err, res) {
                    if (err) {
                        return self.open('reject');
                    }

                    self.globalModalData.transactionHash = res;
                    self.open('success');
                }

                self.binds.update({loading: true, next: true});

                if (self.opened.direction === 'buy') {
                    var address = self.globalModalData.options.refAddress;
                    var refAddress = address && window.web3.isAddress(address) ? address : '0x0';

                    self.metamask.contractMetamask.buy(
                        refAddress,
                        toWei(self.modalData.toAmount),
                        {from: self.metamask.ethAddress, value: toWei(self.modalData.fromAmount), gas: 600000, gasPrice: self.modalData.gasPrice},
                        transactionCompleted
                    );
                } else {
                    (function(callback) {
                        if (self.modalData.gasPrice) {
                            return callback();
                        }

                        getGasPrice(self, function(price) {
                            self.modalData.gasPrice = price;
                            callback();
                        }, self.open.bind('error'));
                    })(function() {
                        self.metamask.contractToken.approve(self.globalModalData.options.etheramaContractAddress, toWei(self.modalData.fromAmount), {from: self.metamask.ethAddress, value: 0, gas: 600000, gasPrice: self.modalData.gasPrice}, function(err, res) {
                            if (err) {
                                return self.open('reject');
                            }

                            setTimeout(function() {
                                self.metamask.contractMetamask.sell(
                                    toWei(self.modalData.fromAmount),
                                    toWei(self.modalData.toAmount),
                                    {from: self.metamask.ethAddress, value: 0, gas: 600000, gasPrice: self.modalData.gasPrice},
                                    transactionCompleted
                                );
                            }, 1000);
                        });
                    });
                }
            },
            fromPercents25: setFromPercents.bind(this, 0.25),
            fromPercents50: setFromPercents.bind(this, 0.5),
            fromPercents75: setFromPercents.bind(this, 0.75),
            fromPercents100: setFromPercents.bind(this, 1)
        });

        /**
         * Loads Eth and token limits
         *
         * @param {Function} callback
         */
        function loadLimits(callback) {
            self.metamask.contractInfura.getEthDealRange(function(err, res) {
                if (!res) {
                    return
                }
                self.modalData.ethLimits = {
                    min: res[0] / Math.pow(10, 18),
                    max: res[1] / Math.pow(10, 18)
                };

                self.metamask.contractInfura.getTokenDealRange(function(err, res) {
                    self.modalData.tokenLimits = {
                        min: res[0] / Math.pow(10, 18),
                        max: res[1] / Math.pow(10, 18)
                    };

                    typeof callback === 'function' && callback();
                });
            });
        }

        /**
         * Inits from value
         */
        function init() {
            if (!self.opened) {
                return;
            }

            self.modalData.checkWeb3Unsubscribe = metaMask.checkWeb3((function() {
                self.opened && (self.opened.direction === 'buy' ? getEthBalance() : getTokenBalance());
            }));

            function getEthBalance() {
                self.modalData.unsubscribeBalanceUpdate && self.modalData.unsubscribeBalanceUpdate();
                self.modalData.unsubscribeBalanceUpdate = self.metamask.updateEthBalance(function(balance) {
                    getGasPrice(self, function(price) {
                        var gasLimit = 600000;
                        var gas = (price * gasLimit) / Math.pow(10, 18);
                        var bal = balance - gas;
                        self.modalData.balance = bal > 0 ? bal : 0;
                        self.modalData.gasPrice = price;
                        recalculate(true, self.modalData.fromAmount || self.modalData.balance, !!self.modalData.fromAmount);
                    }, self.open.bind(self, 'error'));
                });
            }

            function getTokenBalance() {
                // If we don't have MetaMask
                if (!self.metamask.contractMetamask) {
                    self.modalData.balance = 0;
                    recalculate(true, self.modalData.balance);
                    return;
                }

                self.metamask.contractMetamask.getCurrentUserLocalTokenBalance(function(err, res) {
                    self.modalData.balance = res / Math.pow(10, 18);
                    recalculate(true, self.modalData.balance);
                });
            }
            self.opened.direction === 'buy' ? getEthBalance() :  getTokenBalance();
        }

        /**
         * Sets percent of maximum available balance
         *
         * @param {Number} percents
         */
        function setFromPercents(percents) {
            recalculate(true, Math.min(self.modalData.ethLimits.max, self.modalData.balance) * percents);
        }

        /**
         * Checks and highlights errors for From field
         *
         * @returns {boolean}
         */
        function checkFromErrors() {
            if (self.metamask.isLoggedIn) {
                if (self.modalData.fromAmount > self.modalData.balance) {
                    self.binds.update({
                        fromError: 'You don\'t have enough money.'
                    });
                    return true;
                }

                var limits = self.opened ? self.modalData[self.opened.direction === 'buy' ? 'ethLimits' : 'tokenLimits'] : {}
                if (self.modalData.fromAmount < limits.min || self.modalData.fromAmount > limits.max) {
                    self.binds.update({
                        fromError: 'Minimal value is ' + limits.min + '. Maximum value is ' + limits.max + '.'
                    });
                    return true;
                }
            }

            self.binds.update({fromError: ''});
            return false;
        }

        /**
         * Checks and highlights errors for To field
         *
         * @returns {boolean}
         */
        function checkToErrors() {
            if (self.metamask.isLoggedIn && self.opened) {
                var limits = self.modalData[self.opened.direction === 'sell' ? 'ethLimits' : 'tokenLimits']
                if (self.modalData.toAmount < limits.min || self.modalData.toAmount > limits.max) {
                    self.binds.update({
                        toError: 'Minimal value is ' + limits.min + '. Maximum value is ' + limits.max + '.'
                    });
                    return true;
                }
            }

            self.binds.update({toError: ''});
            return false;
        }

        /**
         * Recalculates To value and updates bindings
         *
         * @param {Boolean} isFromField
         * @param {Number} amount
         * @param {Boolean}
         */
        function recalculate(isFromField, amount, ignoreDuplicate) {
            amount = substrValue(amount);

            if (amount <= 0 || (!ignoreDuplicate && self.modalData[isFromField ? 'fromAmount' : 'toAmount'] === amount)) {
                self.modalData[isFromField ? 'fromAmount' : 'toAmount'] = amount;
                self.binds.update(isFromField ? {fromAmount: amount} : {toAmount: amount});
                self.binds.update({loading: false, next: true});
                return;
            }

            self.modalData[isFromField ? 'fromAmount' : 'toAmount'] = amount;
            self.binds.update(isFromField ? {fromAmount: amount} : {toAmount: amount});

            if (isFromField ? !checkFromErrors() : !checkToErrors()) {
                self.binds.update({loading: true, next: true});

                (function(updateTo){
                    if (!isFromField) {
                        return updateTo();
                    }

                    updateTo();
                })(function()
                {
                    self.opened && self.metamask.contractInfura[
                        self.opened.direction === 'buy' ? 'estimateBuyOrder' : 'estimateSellOrder'
                        ](toWei(self.modalData[isFromField ? 'fromAmount' : 'toAmount']), isFromField, function(err, res) {
                        self.modalData[isFromField ? 'toAmount' : 'fromAmount'] = substrValue(res[0] / Math.pow(10, 18));

                        self.binds.update({
                            toAmount: self.modalData.toAmount,
                            fromAmount: self.modalData.fromAmount,
                            fee: noexp(res[1] / Math.pow(10, 18), 18),
                            averagePrice: noexp(res[2] / Math.pow(10, 18), 18),
                            loading: false,
                            next: isFromField ? checkToErrors() : checkFromErrors()
                        });
                    });
                });
            }
        }

        var amountFieldDebounceTimeout;
        /**
         * Amount fields input event listener
         *
         * @param {Boolean} isFromField
         * @param {Object} event
         */
        function changeAmount(isFromField, event) {
            event.target.value = substrValue(event.target.value);

            amountFieldDebounceTimeout && clearTimeout(amountFieldDebounceTimeout);
            amountFieldDebounceTimeout = setTimeout(function()
            {
                recalculate(isFromField, event.target.value);
            }, 500);
        }

        this.binds.event('input', {
            fromAmount: changeAmount.bind(this, true),
            toAmount: changeAmount.bind(this, false)
        });

        /**
         * Magic starts here
         */
        loadLimits(init);
    };

